use std::ffi::CStr;
use windows::core::Interface;
use windows::Win32::Graphics::Direct3D12::*;

pub unsafe fn device_register_message_callback<
    T: Fn(D3D12_MESSAGE_CATEGORY, D3D12_MESSAGE_SEVERITY, D3D12_MESSAGE_ID, &CStr) + 'static,
>(
    device: &ID3D12Device,
    callback: T,
) -> windows::core::Result<u32> {
    /// Internal callback that wraps the closure in an FFI compatible function
    unsafe extern "system" fn raw_callback<
        X: Fn(D3D12_MESSAGE_CATEGORY, D3D12_MESSAGE_SEVERITY, D3D12_MESSAGE_ID, &CStr) + 'static,
    >(
        category: D3D12_MESSAGE_CATEGORY,
        severity: D3D12_MESSAGE_SEVERITY,
        id: D3D12_MESSAGE_ID,
        pdescription: windows::core::PCSTR,
        pcontext: *mut core::ffi::c_void,
    ) {
        // Cast to the concrete type and get a reference
        let context = pcontext as *const X;
        let context = context.as_ref().unwrap();

        let description = CStr::from_ptr(pdescription.0 as *const _);

        // Call the actual callback
        (context)(category, severity, id, description);
    }

    let casted: ID3D12InfoQueue1 = device.cast::<ID3D12InfoQueue1>()?;
    let mut cookie = 0;

    let boxed = Box::new(callback);
    let leak = Box::leak(boxed);

    casted.RegisterMessageCallback(
        Some(raw_callback::<T>),
        D3D12_MESSAGE_CALLBACK_FLAG_NONE,
        leak as *const _ as *const core::ffi::c_void,
        &mut cookie,
    )?;

    Ok(cookie)
}

pub unsafe fn device_unregister_message_callback(
    device: &ID3D12Device,
    cookie: u32,
) -> windows::core::Result<()> {
    let casted: ID3D12InfoQueue1 = device.cast::<ID3D12InfoQueue1>()?;
    casted.UnregisterMessageCallback(cookie)?;
    Ok(())
}

pub const fn category_name(category: &D3D12_MESSAGE_CATEGORY) -> Option<&'static str> {
    match *category {
        D3D12_MESSAGE_CATEGORY_APPLICATION_DEFINED => Some("ApplicationDefined"),
        D3D12_MESSAGE_CATEGORY_MISCELLANEOUS => Some("Miscellaneous"),
        D3D12_MESSAGE_CATEGORY_INITIALIZATION => Some("Initialization"),
        D3D12_MESSAGE_CATEGORY_CLEANUP => Some("Cleanup"),
        D3D12_MESSAGE_CATEGORY_COMPILATION => Some("Compilation"),
        D3D12_MESSAGE_CATEGORY_STATE_CREATION => Some("StateCreation"),
        D3D12_MESSAGE_CATEGORY_STATE_SETTING => Some("StateSetting"),
        D3D12_MESSAGE_CATEGORY_STATE_GETTING => Some("StateGetting"),
        D3D12_MESSAGE_CATEGORY_RESOURCE_MANIPULATION => Some("ResourceManipulation"),
        D3D12_MESSAGE_CATEGORY_EXECUTION => Some("Execution"),
        D3D12_MESSAGE_CATEGORY_SHADER => Some("Shader"),
        _ => None,
    }
}

